        jQuery(function($){
            // Change the price range hidden input's value when change the visible Price range form
            $('#price-from, #price-to').keyup(function() {
                var priceFrom = $('#price-from').val(),
                    priceTo = $('#price-to').val(),
                    priceRange;
                // If priceTo is smaller than priceFrom, switch them.
                if (!priceFrom && !priceTo) {
                    priceRange = '';
                } else if(priceFrom && !priceTo){
                    priceFrom = isNaN(Number(priceFrom))?'': Number(priceFrom);
                    priceRange = priceFrom + '-';
                } else {
                    priceFrom = isNaN(Number(priceFrom))?'':Number(priceFrom);
                    priceTo = isNaN(Number(priceTo))?'':Number(priceTo);
                    priceRange = (priceFrom < priceTo) ? (priceFrom + '-' + priceTo) : (priceTo + '-' + priceFrom);
                }
                // Set price range hidden value
                $('#param-price').val(priceRange);
            });
