<script type="text/javascript">
//<![CDATA[
jQuery(function($) {
    // Display Compare bar when there is compare item
    if ( $('#compare-items > .item').length) {
        $('.compare-bar').show();
    }

    // Add item to compare list when click "Add to compare" in Product grid
    $('.products-grid > li > .add-to-compare').click(function() {
        if ( $('.compare-bar').is(':hidden') ) {
            $('.compare-bar').show();
        }
        var sourseItem = $(this).siblings('.product-id'),
            itemId = sourseItem.text(),
            itemName = $(this).siblings('.more-info').children('.product-name').clone(),
            itemSrc = sourseItem.attr('href');
            itemBrand = $(this).siblings('.product-brand').text(),
            itemMasterCode = $(this).siblings('.product-master-code').text();
        addCompare(itemId, itemName, itemSrc);
    });
    
    // Compare popup
    $('#compare-now').fancybox({
        padding: 0,
        showCloseButton: false,
        onStart: function() {
            $('body').addClass('compare-pop-page');
        }
    });
    
    // Compare now when there are 2 or more items, drop it when not.
    $('#compare-now').click(function(e) {
        e.preventDefault();
        if ( $('#compare-items > .item').length < 2 ) {
            $.fancybox.cancel();
            alert('Minimum 2 items for comparison');
        }
    });
    
    // Clear compare list
    $('#compare-clear').click(function(e) {
        e.preventDefault();
        clearCompare();
    });
    
    // Remove compare item when clicking "[Delete]" in Compare list
    $('#compare-items').delegate('.del', 'click', function() {
        removeCompare( $(this).siblings('.compare-item-id').val() );
    });
    
    // Function: Add item to compare list
    // id: Product id
    // name: Product name
    // src: Product image
    function addCompare(id, name, src) {
        var item = $('#compare-item-' + id);
        
        $.getJSON("https://www.decathlon.tw/zh/decathlon/compare/check/",function(data){
            if(data.code == "false"){
                alert('Maximum 5 items for comparison');
                return false;
            }else{
                if ( !item.length ) {
                    if ( $('#compare-items > .item').length == 5 ) {
                        alert('Maximum 5 items for comparison');
                        return false;
                    }
                    
                    var classId = 'compare-item-' + id,
                        newItem = $('<li />').attr({
                            'class': 'item',
                            id: classId
                        });
                        
                    $('<img />').attr('src', src).after(
                        name.after(
                            $('<span />').attr('class', 'del').text('刪除').after(
                                $('<input />').attr({ 'class': 'compare-item-id', type:'hidden' }).val(id)
                            )
                        )
                    ).appendTo(newItem);
                    
                    $('#compare-items > .empty:first').replaceWith( newItem );
                    $.ajax('https://www.decathlon.tw/zh/decathlon/compare/add/product/' + id);
                } else {
                    removeCompare(id);
                }
            }
        });
        
    }
    
    // Function: Remove compare item
    // id: Product id
    function removeCompare(id) {
        $('#compare-item-' + id).remove();
        $.ajax('https://www.decathlon.tw/zh/decathlon/compare/remove/product/' + id);
        reloadCompare();
    }
    
    // Function: Cleat compare list & Hide compare bar
    function clearCompare() {
        $.ajax('https://www.decathlon.tw/zh/decathlon/compare/clear/');
        
        $('#compare-items').empty();
        reloadCompare();
        $('.compare-bar').hide();
    }
    
    // Function: Add empty items
    function reloadCompare() {
        var times = 5 - $('#compare-items').children().length;
        
        if (times) {
            var emptyTemplate = $('<li />').attr('class', 'empty').text('[Empty]');
            for (i=0; i<times; i++) {
                emptyTemplate.clone().appendTo( $('#compare-items') );
            }
        }
    }
    
    // If it's IE6, control the compare bar.
    if (isIE6()) {
        $(window).bind('scroll resize', function(){
            if ($('.compare-bar').is(':visible')) {
                var compareBarTop = $(document).scrollTop() + $(window).height() - $('.compare-bar').height();
                $('.compare-bar').css('top', compareBarTop);
            }
        });
        $(window).triggerHandler('resize');
    }
});
//]]>
</script> 
